    public function getSourceContext()
    {
        return new Twig_Source("{% extends 'base.html.twig' %}
{% block title %}
  Semester View
{% endblock %}

{% block stylesheet %}
<style>

</style>
{% endblock %}

{% block body %}
  <div class=\"container\" id=\"view_container\">
    <a href=\"{{ path('semester_create') }}\">Create a New semester</a>
    <table border=\"0\">
      <thead>
        <tr>
          <th>Semester</th>
          <th>Year</th>
          <th>Curriculum</th>
          <th colspan=\"3\">Actions</th>
        </tr>
      </thead>
      <tbody>
        {% for semester in semesters %}
          <tr>
            <td>{{ semester.semester }}</td>
            <td>{{ semester.year }}</td>
            <td>
              {% for curriculum in curriculums %}
                {% if(semester.curriculumId == curriculum.id) %}
                  {{ curriculum.curriculumName }}
                {% endif %}
              {% endfor %}
            </td>

            {% if(app.session.get('user_type') == 'admin') %}
              <td><a href=\"{{ path('semester_edit', {'semester_id': semester.id}) }}\" id=\"editBtn\">edit</button></td>
              <td><a href=\"{{ path('semester_delete', {'semester_id': semester.id}) }}\" id=\"deleteBtn\"
                    onclick=\"return confirm('Are You Sure You Want To Delete {{ semester.semester }} ({{ semester.year }})?') \">delete</button></td>
            {% endif %}
          </tr>
        {% endfor %}
      </tbody>
    </table>
  </div>
{% endblock %}
", "semester/view.html.twig", "/home/apollo-19/github/B-A-Project/app/Resources/views/semester/view.html.twig");
