        return new Twig_Source("{% extends 'base.html.twig' %}
{% block title %}
  School Assessments View
{% endblock %}

{% block stylesheet %}
<style>

</style>
{% endblock %}

{% block body %}
  <div class=\"container\" id=\"view_container\">
    <a href=\"{{ path('assessment_type_create') }}\">New School Assessment</a>
    <table border=\"0\">
      <thead>
        <tr>
          <th>Assessment Name</th>
          <th>Assessment Worth</th>
          <th colspan=\"3\">Actions</th>
        </tr>
      </thead>
      <tbody>
        {% for assessment in assessment_types %}
          <tr>
            <td>{{ assessment.assessmentName }}</td>
            <td>{{ assessment.assessmentWorth }}</td>

            {% if(app.session.get('user_type') == 'admin') %}
              <td><a href=\"{{ path('assessment_type_edit', {'assessment_type_id': assessment.id}) }}\" id=\"editBtn\">edit</button></td>
              <td><a href=\"{{ path('assessment_type_delete', {'assessment_type_id': assessment.id}) }}\" id=\"deleteBtn\"
                    onclick=\"return confirm('Are You Sure You Want To Delete {{ assessment.assessmentName ~ ' (' ~ assessment.assessmentWorth }})?') \">delete</button></td>
            {% endif %}
          </tr>
        {% endfor %}
      </tbody>
    </table>
  </div>
{% endblock %}
